ifMode.script:
jq(document).ready(function() {
    const aliveConceptId = '';
    const noConceptId = '';

    jq("span[id^='dead-or-alive-']").find("input[type='radio']").change(function(event) {
        if (this.checked) {
            // navigate up the DOM to the parent .fetus div for this baby
            let parentDiv = jq(this).closest(".fetus");
            if (parentDiv) {
                //find the enclosed Register in EMR obs associated with this .fetus div
                let nextRegisterBaby = jq(parentDiv).find("span[id^='register-baby-']");
                if (nextRegisterBaby) {
                    let obsValue = jq(this).prop('value');
                    let isRegisteredInEMR = jq(nextRegisterBaby).find("input[type='radio'][value='" + noConceptId + "']");
                    if (isRegisteredInEMR) {
                        if ( obsValue === aliveConceptId) {
                            jq(isRegisteredInEMR).prop("checked", true);
                        }
                    }
                }
            }
        }
    });
});


let hasDeliveryTime = function(element, required) {
    let hasValue = false;
    let deliveryDate = jq(element).find('.hasDatepicker').first();
    if ( deliveryDate ) {
        let dateVal = deliveryDate.datepicker('getDate');
        if ( dateVal ) {
          hasValue = true;
          jq(deliveryDate).parent().find('.field-error').text('').hide();
        } else if ( required ) {
          jq(deliveryDate).parent().find('.field-error').text('*Required').show();
        }
    }
    return hasValue;
}

let hasGender = function(element, required) {
    let hasValue = false;
    let genderElem = jq(element).find('.fetusGender').first();
    if ( genderElem ) {
        let genderVal = jq(genderElem).find("input[type='radio']:checked").first().val();
        if ( genderVal ) {
          hasValue = true;
          jq(genderElem).parent().find('.field-error').text('').hide();
        } else if ( required ) {
          jq(genderElem).parent().find('.field-error').css('display', 'inline-block').text('*Required').show();
        }
    }
    return hasValue;
  }

  let hasOutcome = function(element, required) {
    let hasValue = false;
    let outcomeElem = jq(element).find('.fetusOutcome').first();
    if ( outcomeElem ) {
        let outcomeVal = jq(outcomeElem).find("input[type='radio']:checked").first().val();
        if ( outcomeVal ) {
          hasValue = true;
          jq(outcomeElem).parent().find('.field-error').text('').hide();
        } else if ( required ) {
            jq(outcomeElem).parent().find('.field-error').css('display', 'inline-block').text('*Required').show();
        }
    }
    return hasValue;
  }

  clearRequiredMessages = function( element, classArray) {
    classArray.forEach( (className) => {
      let item = jq(element).find('.' + className).first();
      if ( item ) {
        jq(item).parent().find('.field-error').text('').hide();
       }
    });
  }

    validateFetusEntries = function(element) {
        let deliveryTime = hasDeliveryTime(element, true);
        let gender = hasGender(element, true);
        let outcome = hasOutcome(element, true);
        return ( deliveryTime && gender && outcome );
    }

    updateFetusEntries = function(element) {
        let fetusElem = jq(element).closest("span.fetus");
        if ( fetusElem ) {
            if ( hasDeliveryTime(jq(fetusElem)) || hasGender(jq(fetusElem)) || hasOutcome(jq(fetusElem))) {
              validateFetusEntries(jq(fetusElem));
            } else {
                //clear all the '*Required' messages for this fetus
                clearRequiredMessages(jq(fetusElem), ['hasDatepicker', 'fetusGender', 'fetusOutcome']);
            }
        }
    }

htmlForm.getBeforeValidation().push(function() {
  let valid = false;
  jq('span.fetus').each(function() {
      if ( hasDeliveryTime(jq(this)) || hasGender(jq(this)) || hasOutcome(jq(this))) {
        valid = validateFetusEntries(jq(this));
        if ( !valid ) {
          jq(document).scrollTop(jq(this).offset().top -100);
          // stop at the first invalid group of entries
          return valid;
        }
      } else {
          valid = true;
      }
  });
  return valid;
});

jq(document).ready(function() {

    jq('span.fetusDeliveryTime input.hasDatepicker').change(function() {
      updateFetusEntries(jq(this));
     });

    jq("span .fetusGender input[type='radio']").change(function() {
      updateFetusEntries(jq(this));
    });

    jq("span .fetusOutcome input[type='radio']").change(function() {
      updateFetusEntries(jq(this));
    });

    // Set up EDD and Gestational Age
    jq("#calculated-edd-and-gestational").hide();
    const lastLMP = '';
    const currentEncounterDate = '';
    const msgWeeks = '';
    if (typeof lastLMP !== "undefined" && lastLMP !== null && (lastLMP.length > 0)) {
        const lastPeriodDate = new Date(lastLMP);
        const gestAgeText = calculateGestationalDays(lastPeriodDate, currentEncounterDate, msgWeeks);
        jq("#calculated-gestational-age-value").text(gestAgeText);
        const edd = calculateExpectedDeliveryDate(lastPeriodDate);
        const locale = window.sessionContext.locale || navigator.language;
        jq("#calculated-edd").text((Intl.DateTimeFormat(locale, { dateStyle: "medium" })).format(edd));
        jq("#calculated-edd-and-gestational").show();
    }
    // handlers for next and submit buttons, see nextAndSubmitButtons.js
    setUpNextAndSubmitButtons();

    //disable all radio buttons associated with the Register in EMR question
    // (this obs gets set automatically based on the other questions answers)
    jq("span[id^='register-baby']").find("input[type='radio']").attr("disabled", true);

    function calculateTotalLaborTime() {
        let totalHours = 0;
        let totalMinutes = 0;
        jq(".labor_stage_hours").find("input[type='text']").each( function() {
            totalHours = +totalHours + +(jq(this).val());
        });

        jq(".labor_stage_minutes").find("input[type='text']").each( function() {
            totalMinutes = +totalMinutes + +(jq(this).val());
        });

        if ( totalHours ) {
            if ( totalMinutes  && totalMinutes  >= 60) {
                totalHours = +totalHours + Math.floor(totalMinutes / 60);
                totalMinutes = +(totalMinutes % 60);
            }
        }
        jq("#totalDuration_hours").find("input[type='text']").val(totalHours);
        jq("#totalDuration_minutes").find("input[type='text']").val(totalMinutes);
    }

    jq(".labor_stage_hours").find("input[type='text']").change( function(event) {
        calculateTotalLaborTime();
    });
    jq(".labor_stage_minutes").find("input[type='text']").change( function(event) {
        calculateTotalLaborTime();
    });

    beforeSubmit.push(function() {
        //re-enable the register patient in EMR obs so they could be saved
        jq("span[id^='register-baby']").find("input[type='radio']").attr("disabled", false);
        return true;

    });
});

htmlform.script:
jq(document).ready(function() {

    const laborProgressEncounterType = 'ac5ec970-31b7-4659-9141-284bfbc13c69';
    const laborStartDatetimeConceptUuid = '';
    const inducedConceptUuid         = '';
    const inducedDatetimeConceptUuid = '';
    const inductionMethodConceptUuid = '';
    const mechInducConceptUuid = '';
    const oxyConceptUuid = '';
    const misoConceptUuid = '';
    const yesConceptUuid = '';
    const noConceptUuid  = '';

    const contextPath = window.location.href.split('/')[3];
    const apiBaseUrl =  "/" + contextPath + "/ws/rest/v1";
    const options = { weekday: 'long', year: 'numeric', month: 'short', day: 'numeric' };
    const monthOption = { month: 'short'};

    const patientUuid = '';
    const currentEncUuid = '';
    let editingCurrentObs = false;
    if (currentEncUuid) {
        let encHasLabourStartObs = false;
        const lbrStartField = jq("#labourStartDateTime .hasDatepicker");
        if ( lbrStartField.length > 0) {
            if (lbrStartField.datepicker('getDate') ) {
                encHasLabourStartObs = true;
             }
       }
        if ( encHasLabourStartObs || getValue('induced.value')) {
            // the current encounter already has Labour Progress obs
            editingCurrentObs = true;
        }
    }
    if ( !editingCurrentObs ) {                        

        let currentEncounterDate = '';
        // look for the encounterDate datepicker widget
        let encounterDateValue = jq("#encounterDate .hasDatepicker");
        if (encounterDateValue) {
            let getDate = encounterDateValue.datepicker('getDate');
            if (getDate) {
                currentEncounterDate = new Date(getDate);
            }
        }

        if ( currentEncounterDate ) {
            const today = new Date();
            if ( (today.getFullYear() == currentEncounterDate.getFullYear())
                    && (today.getMonth() == currentEncounterDate.getMonth())
                    && (today.getDate() == currentEncounterDate.getDate())) {
                // encounter is from today, therefore we need to add the time component, the current time of the day
                currentEncounterDate = today;
            }
            let nextDayFromEncDate = new Date(currentEncounterDate);
            nextDayFromEncDate.setDate(nextDayFromEncDate.getDate() + 1);
            let restEncMonth = nextDayFromEncDate.getMonth() + 1;
            let restEncDay = nextDayFromEncDate.getDate(); // to catch all encounters from today
            let restEncDate = nextDayFromEncDate.getFullYear() + '-' + restEncMonth + '-' + restEncDay;
            // looking for a previous Labour Progress encounter within the same visit
            jq.getJSON(apiBaseUrl + "/encounter", {                    
                patient: patientUuid,
                encounterType: laborProgressEncounterType,
                todate: restEncDate,
                order: 'desc',
                v: 'custom:(uuid,patient:(uuid),encounterType:(uuid),visit:(uuid,startDatetime,stopDatetime),encounterDatetime,voided,obs:(uuid,display,concept:(uuid,display),obsDatetime,valueCoded:(uuid,display),valueDatetime,valueNumeric,valueText,groupMembers:(uuid,display,person:(uuid),concept:(uuid,display),obsDatetime,valueCoded:(uuid,display),valueDatetime,valueNumeric,valueText,voided),voided)'                    
              }, function( data ) {
                if (data.results.length > 0) {
                    for (let index = 0; index < data.results.length; index++) {
                        let laborProgressEnc = data.results[index];
                        let encDatetime = new Date(laborProgressEnc.encounterDatetime);
                        if ( laborProgressEnc.visit && laborProgressEnc.visit.startDatetime ) {                                
                            let visitStartDatetime = new Date(laborProgressEnc.visit.startDatetime);
                            if ( (visitStartDatetime.getTime()  <=  currentEncounterDate.getTime())
                                && (!laborProgressEnc.visit.stopDatetime || (new Date(laborProgressEnc.visit.stopDatetime)).getTime() >=  currentEncounterDate.getTime())
                                && (encDatetime.getTime() <= currentEncounterDate.getTime())) {
                                //our form's encounter date falls within this visit
                                jq("#lastOnset").show();
                                jq("#enter_labour_progress").hide();
                                let month = new Intl.DateTimeFormat(undefined, monthOption).format(encDatetime);    
                                jq("#lastLaborProgressEncounterDate").text(encDatetime.getDate() + "-" + month + "-" + encDatetime.getFullYear());
                                jq("#lastLaborProgressEncounterTime").text(encDatetime.toLocaleTimeString());  
                                jq("#lastLaborProgressEncounterDate").removeClass("emptyValue").addClass("value");    
                                jq("#lastLaborProgressEncounterTime").removeClass("emptyValue").addClass("value");  
                                if (laborProgressEnc.obs && laborProgressEnc.obs.length > 0) {
                                    for (let j=0; j < laborProgressEnc.obs.length; j++) { 
                                        let obs = laborProgressEnc.obs[j];
                                        if (obs.concept.uuid === laborStartDatetimeConceptUuid) {
                                            if ( obs.valueDatetime ) {
                                                let laborStartDate = new Date(obs.valueDatetime);
                                                jq("#last_labor_start_date").text(laborStartDate.getDate() + " " + new Intl.DateTimeFormat(undefined, monthOption).format(laborStartDate) + " " + laborStartDate.getFullYear());
                                                jq("#last_labor_start_date").removeClass("emptyValue").addClass("value");
                                                jq("#last_labor_start_time").text(laborStartDate.toLocaleTimeString());  
                                                jq("#last_labor_start_time").removeClass("emptyValue").addClass("value");
                                            }
                                        } else if (obs.concept.uuid === inducedConceptUuid) {
                                            if ( obs.valueCoded ) {
                                                switch ( obs.valueCoded.uuid ) {
                                                    case yesConceptUuid:
                                                        jq("#yes_induction").text("[X] " + obs.valueCoded.display);
                                                        jq("#yes_induction").removeClass("emptyValue").addClass("value");
                                                        break;
                                                    case noConceptUuid:
                                                        jq("#no_induction").text("[X] " + obs.valueCoded.display);
                                                        jq("#no_induction").removeClass("emptyValue").addClass("value");
                                                        break;
                                                }
                                            }                                                
                                        } else if (obs.concept.uuid === inducedDatetimeConceptUuid) {
                                            if ( obs.valueDatetime ) {
                                                let inducedDatetime = new Date(obs.valueDatetime);
                                                jq("#last_induced_date").text(inducedDatetime.getDate() + " " + new Intl.DateTimeFormat(undefined, monthOption).format(inducedDatetime) + " " + inducedDatetime.getFullYear());
                                                jq("#last_induced_date").removeClass("emptyValue").addClass("value");
                                                jq("#last_induced_time").text(inducedDatetime.toLocaleTimeString());  
                                                jq("#last_induced_time").removeClass("emptyValue").addClass("value");  
                                            }
                                        } else if (obs.concept.uuid === inductionMethodConceptUuid) {
                                            if ( obs.valueCoded ) {
                                                switch ( obs.valueCoded.uuid ) {
                                                    case mechInducConceptUuid:
                                                        jq("#mech_induction").text("[X] Mechanical induction");
                                                        jq("#mech_induction").removeClass("emptyValue").addClass("value");
                                                        break;
                                                    case oxyConceptUuid:
                                                        jq("#oxy_induction").text("[X] Oxytocin" );
                                                        jq("#oxy_induction").removeClass("emptyValue").addClass("value");
                                                        break;
                                                    case misoConceptUuid:
                                                        jq("#miso_induction").text("[X] Misoprostol");
                                                        jq("#miso_induction").removeClass("emptyValue").addClass("value");
                                                        break;
                                                }
                                            }
                                        }
                                    }
                                }    
                                // no need to continue iterating over the list of encounters. 
                                // we are only interested in the last Labor Progress encounter   
                                break;
                            }
                        }
                    }
                }
              });
        }
    }
});

ifMode.script:
jq(function() {

    var hasValue = function(element) {
        return jq(element).find('.fetus-name input').val();
    };

    var hideOtherFetus = function() {
        jq('#fetus-2').hide();
        jq('#fetus-3').hide();
    };

    if (!hasValue('#fetus-2')) {
        hideOtherFetus();
        jq('#show-more-fetus-button').show();
    }

    if (!hasValue('#fetus-3')) {
        hideOtherFetus();
        jq('#show-more-fetus-button').show();
    }

    jq('#show-more-fetus-button').click(function() {
        jq('.fetus').show();
        jq('#show-more-fetus-button').hide();
        jq('#show-less-fetus-button').show();
    });

    jq('#show-less-fetus-button').click(function() {
        hideOtherFetus();
        jq('#show-less-fetus-button').hide();
        jq('#show-more-fetus-button').show();
    });

})
